mapper.select:
SELECT id, user_id, bank_id, money, is_locked, version, gmt_create, gmt_modified, is_delete
FROM sys_account


        id = #{id}


        AND user_id = #{userId}


        AND bank_id = #{bankId}


        AND money = #{money}


        AND is_locked = #{locked}


        AND version = #{version}


        AND gmt_create = #{gmtCreate}


        AND gmt_modified = #{gmtModified}


        AND is_delete = #{delete}

mapper.update:
UPDATE sys_account
SET is_delete = 1,
    version=version + 1
WHERE id = #{id}
  AND version = #{version}
  AND is_delete = 0
